            "intro.tagline": "Make Your $10 Mouse Better Than an Apple Trackpad!",
            "intro.big-tagline": "Make Your $10 Mouse Better Than an Apple Trackpad!",
            "navbar.title": "Mac Mouse Fix",
            "navbar.links.overview": "Overview",
